template.css:
.card {
  background: var(--card-bg, #ffffff);
  border-radius: var(--card-radius, 8px);
  padding: var(--card-padding, 16px);
  border: 1px solid var(--card-border, #f3f4f6);
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.card-inner {
  display: flex;
  flex-direction: column;
  width: 100%;
}

.card-image {
  position: relative;
  width: 100%;
  aspect-ratio: 1/1;
  margin-bottom: 12px;
  overflow: hidden;
  border-radius: 4px;
}

.card-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}

.card-content {
  display: flex;
  flex-direction: column;
  flex: 1;
}

.card-subtitle {
  font-size: var(--subtitle-font-size, 12px);
  font-weight: var(--subtitle-font-weight, 500);
  color: var(--subtitle-color, #9ca3af);
  text-transform: var(--subtitle-text-transform, uppercase);
  letter-spacing: var(--subtitle-letter-spacing, 0);
  margin-bottom: 2px;
}

.card-title {
  font-size: var(--title-font-size, 16px);
  font-weight: var(--title-font-weight, 500);
  color: var(--title-color, #111827);
  margin: 0 0 6px 0;
  line-height: var(--title-line-height, 1.4);
}

.card-description {
  font-size: var(--description-font-size, 13px);
  font-weight: var(--description-font-weight, 400);
  color: var(--description-color, #6b7280);
  line-height: var(--description-line-height, 1.5);
  margin: 0 0 10px 0;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

.card-footer {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-top: auto;
}

.card-price {
  font-size: var(--price-font-size, 18px);
  font-weight: var(--price-font-weight, 600);
  color: var(--price-color, #111827);
}
