template.css:
.card {
  background: var(--card-bg, #ffffff);
  border-radius: var(--card-radius, 16px);
  padding: var(--card-padding, 20px);
  border: 1px solid var(--card-border, #e5e7eb);
  box-shadow: 0 4px 6px rgba(0,0,0,0.1);
  overflow: hidden;
}

.card-image {
  position: relative;
  width: 100%;
  aspect-ratio: 1/1;
  margin-bottom: 16px;
  overflow: hidden;
  border-radius: 12px;
}

.card-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.card-content {
  display: flex;
  flex-direction: column;
}

.card-subtitle {
  font-size: var(--subtitle-font-size, 13px);
  font-weight: var(--subtitle-font-weight, 500);
  color: var(--subtitle-color, #7c2bee);
  text-transform: var(--subtitle-text-transform, uppercase);
  letter-spacing: var(--subtitle-letter-spacing, 0.5px);
  margin-bottom: 4px;
}

.card-title {
  font-size: var(--title-font-size, 18px);
  font-weight: var(--title-font-weight, 600);
  color: var(--title-color, #1f2937);
  margin: 0 0 8px 0;
  line-height: var(--title-line-height, 1.3);
}

.card-description {
  font-size: var(--description-font-size, 14px);
  font-weight: var(--description-font-weight, 400);
  color: var(--description-color, #6b7280);
  line-height: var(--description-line-height, 1.6);
  margin: 0 0 12px 0;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

.card-footer {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-top: auto;
}

.card-price {
  font-size: var(--price-font-size, 22px);
  font-weight: var(--price-font-weight, 700);
  color: var(--price-color, #7c2bee);
}
