template.css:
.card {
  background: var(--card-bg, #ffffff);
  border-radius: var(--card-radius, 12px);
  padding: var(--card-padding, 24px);
  border: 1px solid var(--card-border, #e5e7eb);
  box-shadow: var(--card-shadow, 0 4px 6px -1px rgba(0,0,0,0.05));
  display: flex;
  gap: 24px;
  transition: all var(--animation-duration, 0.3s) ease;
  overflow: hidden;
}

.card:hover {
  transform: var(--hover-transform, translateY(-4px));
  box-shadow: var(--hover-shadow, 0 8px 12px -2px rgba(0,0,0,0.1));
}

.card-header {
  margin-bottom: 16px;
}

.card-category {
  font-size: var(--title-font-size, 14px);
  font-weight: var(--title-font-weight, 600);
  color: var(--title-color, #1f2937);
  line-height: var(--title-line-height, 1.4);
  letter-spacing: var(--title-letter-spacing, 0.5px);
  text-align: var(--title-text-align, left);
  text-transform: var(--title-text-transform, uppercase);
  display: inline-block;
  position: relative;
}

.card-category::before {
  content: "◆ ";
  color: #6b7280;
  font-size: 0.8em;
}

.card-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

.quote {
  font-size: var(--description-font-size, 22px);
  font-weight: var(--description-font-weight, 400);
  color: var(--description-color, #1f2937);
  line-height: var(--description-line-height, 1.5);
  letter-spacing: var(--description-letter-spacing, 0);
  text-align: var(--description-text-align, left);
  text-transform: var(--description-text-transform, none);
  margin: 0 0 24px 0;
  font-style: italic;
}

.author-info {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.author-name {
  font-size: var(--author-font-size, 16px);
  font-weight: var(--author-font-weight, 500);
  color: var(--author-color, #1f2937);
  line-height: var(--author-line-height, 1.4);
  letter-spacing: var(--author-letter-spacing, 0);
  text-align: var(--author-text-align, left);
  text-transform: var(--author-text-transform, none);
}

.author-role {
  font-size: var(--role-font-size, 14px);
  font-weight: var(--role-font-weight, 400);
  color: var(--role-color, #6b7280);
  line-height: var(--role-line-height, 1.4);
  letter-spacing: var(--role-letter-spacing, 0);
  text-align: var(--role-text-align, left);
  text-transform: var(--role-text-transform, none);
}

.card-image {
  width: 40%;
  height: auto;
  border-radius: 8px;
  overflow: hidden;
  position: relative;
}

.card-image img {
  width: 100%;
  height: auto;
  object-fit: cover;
  transition: transform var(--animation-duration, 0.3s) ease;
}

@media (max-width: 768px) {
  .card {
    flex-direction: column;
  }

  .card-image {
    order: -1;
    width: 100%;
    height: 200px;
  }
}
