template.css:
.card-article {
  background: var(--card-bg, #ffffff);
}

.card-link {
  text-decoration: none;
  color: inherit;
  display: block;
}

.card-image-wrapper {
  width: 100%;
  aspect-ratio: 16/9;
  margin-bottom: 24px;
  overflow: hidden;
}

.card-image-wrapper img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.4s ease;
}

.card-link:hover .card-image-wrapper img {
  transform: scale(1.03);
}

.card-info {
  padding: 0;
}

.card-meta {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 12px;
}

.card-date {
  font-size: 13px;
  color: #64748b;
}

.card-category {
  font-size: 13px;
  color: #7c2bee;
  font-weight: 600;
}

.card-title {
  font-size: 24px;
  font-weight: 700;
  color: #1e293b;
  margin: 0 0 12px 0;
  line-height: 1.3;
}

.card-excerpt {
  font-size: 16px;
  color: #475569;
  line-height: 1.6;
  margin: 0 0 20px 0;
}

.card-author {
  display: flex;
  align-items: center;
  gap: 12px;
}

.card-author img {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  object-fit: cover;
}

.card-author span {
  font-size: 14px;
  color: #475569;
  font-weight: 500;
}
