template.css:
.card {
  background: var(--card-bg, #ffffff);
  border-radius: var(--card-radius, 16px);
  padding: var(--card-padding, 20px);
  border: 1px solid var(--card-border, #e5e7eb);
  box-shadow: var(--card-shadow, 0 4px 6px rgba(0,0,0,0.1));
  box-sizing: border-box;
  overflow: hidden;
  transition: all 0.3s ease;
  display: flex;
  flex-direction: column;
}

.card:hover {
  transform: translateY(-4px);
  box-shadow: 0 20px 25px -5px rgba(0,0,0,0.1);
}

.card-inner {
  display: flex;
  flex-direction: column;
  width: 100%;
}

.card-image {
  position: relative;
  width: 100%;
  aspect-ratio: 1/1;
  margin-bottom: 16px;
  overflow: hidden;
  border-radius: 12px;
}

.card-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
  transition: transform 0.3s ease;
}

.card:hover .card-image img {
  transform: scale(1.05);
}

.card-badge {
  position: absolute;
  top: 8px;
  right: 8px;
  background: #ef4444;
  color: white;
  padding: 4px 8px;
  border-radius: 4px;
  font-size: 12px;
  font-weight: 600;
}

.card-content {
  display: flex;
  flex-direction: column;
}

.card-category {
  font-size: 12px;
  color: #6b7280;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-bottom: 4px;
}

.card-title {
  font-size: 18px;
  font-weight: 600;
  color: #1f2937;
  margin: 0 0 8px 0;
  line-height: 1.3;
}

.card-desc {
  font-size: 14px;
  color: #6b7280;
  line-height: 1.5;
  margin: 0 0 16px 0;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

.card-footer {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-top: auto;
}

.card-price {
  font-size: 24px;
  font-weight: 700;
  color: #7c2bee;
}

.card-btn {
  background: #7c2bee;
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  transition: background 0.2s ease;
}

.card-btn:hover {
  background: #6b21a8;
}
