template.css:
.card {
  background: var(--card-bg, #ffffff);
  border-radius: var(--card-radius, 12px);
  padding: var(--card-padding, 20px);
  border: 1px solid var(--card-border, #e5e7eb);
  box-shadow: var(--card-shadow, 0 4px 6px -1px rgba(0,0,0,0.05));
  display: flex;
  align-items: flex-start;
  gap: 16px;
  transition: all 0.3s ease;
  overflow: hidden;
}

.card:hover {
  transform: translateY(-4px);
  box-shadow: 0 8px 12px -2px rgba(0,0,0,0.1);
}

.card-inner {
  display: flex;
  align-items: flex-start;
  gap: 16px;
  width: 100%;
}

.card-avatar {
  flex-shrink: 0;
  width: 60px;
  height: 60px;
  border-radius: 8px;
  overflow: hidden;
  background: #f3f4f6;
}

.card-avatar img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.card-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.author-info {
  display: flex;
  flex-direction: column;
  gap: 2px;
}

.author-name {
  font-size: var(--author-font-size, 16px);
  font-weight: var(--author-font-weight, 500);
  color: var(--author-color, #111827);
  line-height: var(--author-line-height, 1.3);
  letter-spacing: var(--author-letter-spacing, 0);
  text-align: var(--author-text-align, left);
  text-transform: var(--author-text-transform, none);
}

.author-role {
  font-size: var(--role-font-size, 13px);
  font-weight: var(--role-font-weight, 400);
  color: var(--role-color, #6b7280);
  line-height: var(--role-line-height, 1.4);
  letter-spacing: var(--role-letter-spacing, 0);
  text-align: var(--role-text-align, left);
  text-transform: var(--role-text-transform, none);
}

.testimonial-text {
  font-size: var(--description-font-size, 14px);
  font-weight: var(--description-font-weight, 400);
  color: var(--description-color, #374151);
  line-height: var(--description-line-height, 1.6);
  letter-spacing: var(--description-letter-spacing, 0);
  text-align: var(--description-text-align, left);
  text-transform: var(--description-text-transform, none);
  margin: 0;
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
}
